package control.loop;

public class NineNineTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int x;
		int y;
		
		for ( x=1;x<=9 ; x++ ) {
			for(y=1; y<=9; y++) {
				
				System.out.printf("%dx%d = %d%n",x,y,x*y);
				
				
			}
			
			
		}
		
		
		
		
	}

}
